//second approach
import React, {useState, useEffect} from 'react';
import axios from 'axios';
import timeStamp from '../../apis/blogpost';
import {FETCH_POSTS} from "../../actions/types";

const SearchBlog = () => {
    const [term, setTerm] = useState('programming');
    const [debouncedTerm, setDebouncedTerm] = useState(term);
    const [results, setResults] = useState([]);
    //first args is function
    //second args is when code is executed, second args controls the one of three scenario

    // console.log('i tun everytime');

    // console.log(results);


    useEffect(() => {
        const timerId = setTimeout(() => {
            setDebouncedTerm(term)
        }, 1000);

        return () => {
            clearTimeout(timerId);
        }
    }, [term]);

    useEffect(() => {
        const search = async () => {
            // const {data} = await axios.get('https://en.wikipedia.org/w/api.php', {
            //
            //     params: {
            //         action: 'query',
            //         list: 'search',
            //         origin: '*',
            //         format: 'json',
            //         srsearch: debouncedTerm
            //     }
            // });

            const {data} = await timeStamp.get('/blogPost');

            // dispatch({type: FETCH_POSTS, payload: response.data})

            console.log("data", data);
            // console.log("data query", data.query.search);
            setResults(data);
        };
        search();

    }, [debouncedTerm]);

    const renderedResults = results.map((result) => {
        return (
            <div key={result.pageid} className={`item`}>
                <div className={`right floated content`}>
                    <a
                        href={`https://en.wikipedia.org?curid=${result.pageid}`}
                        className={`ui button`}>
                        Go
                    </a>
                </div>
                <div className={`content`}>
                    <div className={`header`}>
                        {result.title}
                    </div>
                    <span dangerouslySetInnerHTML={{__html: result.snippet}}></span>
                    {/*{result.snippet}*/}
                </div>
            </div>
        )
    });
    return (
        <div>
            <div className={`ui form`}>
                <div className={`field`}>
                    <label htmlFor="">Enter Search Term</label>
                    <input
                        className={`input`}
                        value={term}
                        onChange={e => setTerm(e.target.value)}
                    />
                </div>
            </div>
            <div className={`ui celled list`}>
                {renderedResults}
            </div>
        </div>
    )
};

export default SearchBlog;
